import React, { useContext } from 'react';
import { ThemeContext } from '../contexts/ThemeContext';
import { Sun, Moon, Monitor } from 'lucide-react';

const ThemeToggle = () => {
  const { theme, setTheme } = useContext(ThemeContext);

  return (
    <div className="dropdown dropdown-end">
      <div tabIndex={0} role="button" className="btn btn-ghost btn-square">
        {theme === 'light' && <Sun />}
        {theme === 'dark' && <Moon />}
        {theme === 'system' && <Monitor />}
      </div>
      <ul tabIndex={0} className="dropdown-content menu bg-base-100 rounded-box z-[1] w-52 p-2 shadow">
        <li>
          <button type="button" className="w-full text-left" onClick={() => setTheme('light')}>
            Light
          </button>
        </li>
        <li>
          <button type="button" className="w-full text-left" onClick={() => setTheme('dark')}>
            Dark
          </button>
        </li>
        <li>
          <button type="button" className="w-full text-left" onClick={() => setTheme('system')}>
            System
          </button>
        </li>
      </ul>
    </div>
  );
};

export default ThemeToggle;
