import React from 'react';

const images = [
  {
    id: 'india',
    title: 'India',
    alt: 'Taj Mahal at sunrise, India',
    src: 'https://images.unsplash.com/photo-1549880338-65ddcdfd017b?auto=format&fit=crop&w=1400&q=80'
  },
  {
    id: 'usa',
    title: 'USA',
    alt: 'New York skyline at dusk, USA',
    src: 'https://images.unsplash.com/photo-1505765050928-52f6f4b5b8f8?auto=format&fit=crop&w=1400&q=80'
  },
  {
    id: 'malaysia',
    title: 'Malaysia',
    alt: 'Petronas Twin Towers, Kuala Lumpur, Malaysia',
    src: 'https://images.unsplash.com/photo-1491553895911-0055eca6402d?auto=format&fit=crop&w=1400&q=80'
  },
  {
    id: 'dubai',
    title: 'Dubai',
    alt: 'Burj Khalifa skyline, Dubai',
    src: 'https://images.unsplash.com/photo-1542317854-9f6a2b88dc2a?auto=format&fit=crop&w=1400&q=80'
  }
];

const HeroImages = () => {
  return (
    <section className="w-full max-w-5xl mx-auto mb-6">
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {images.map((img, idx) => (
          <div
            key={img.id}
            className={`relative overflow-hidden rounded-2xl shadow-lg bg-gray-100 dark:bg-gray-800 transform transition-all duration-400 hover:scale-105 hover:-translate-y-1 z-0`}
            style={{ minHeight: 140 }}
          >
            <img
              src={img.src}
              alt={img.alt}
              className="w-full h-full object-cover block pop-up-image"
              style={{ display: 'block' }}
            />

            <div className="absolute left-3 bottom-3 px-3 py-1 rounded-full bg-white/75 dark:bg-black/50 text-sm font-semibold text-gray-800 dark:text-gray-100 backdrop-blur">
              {img.title}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default HeroImages;
