import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const TripResults = () => {
  const { tripId } = useParams();
  const navigate = useNavigate();
  const [trip, setTrip] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('overview');
  const [saved, setSaved] = useState(false);

  const fetchTrip = useCallback(async () => {
    try {
      const response = await axios.get(`/api/trips/${tripId}`);
      setTrip(response.data);
    } catch (err) {
      setError('Failed to load trip details');
      console.error('Error fetching trip:', err);
    } finally {
      setLoading(false);
    }
  }, [tripId]);

  useEffect(() => {
    if (tripId && tripId !== 'null' && tripId !== 'undefined') {
      fetchTrip();
    } else {
      setError('Invalid trip ID');
      setLoading(false);
    }
  }, [fetchTrip, tripId]);

  const handleSaveTrip = async () => {
    try {
      await axios.post(`/api/trips/${tripId}/save`);
      setSaved(true);
    } catch (err) {
      console.error('Error saving trip:', err);
    }
  };

  const handleBookNow = () => {
    navigate(`/booking/${tripId}`);
  };

  const handleExportPdf = async () => {
    try {
      const response = await axios.get(`/api/export/pdf/${tripId}`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `trip-plan-${tripId}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Error exporting PDF:', err);
    }
  };

  const handleExportExcel = async () => {
    try {
      const response = await axios.get(`/api/export/excel/${tripId}`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `trip-plan-${tripId}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Error exporting Excel:', err);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-base-100">
        <div className="card bg-base-100 shadow-xl max-w-md">
          <div className="card-body text-center">
            <span className="loading loading-spinner loading-lg text-primary"></span>
            <h2 className="card-title justify-center text-2xl">Loading trip details...</h2>
            <p className="text-base-content/60">Preparing your perfect vacation plan</p>
          </div>
        </div>
      </div>
    );
  }

  if (error || !trip) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-base-100">
        <div className="card bg-base-100 shadow-xl max-w-lg">
          <div className="card-body">
            <div className="alert alert-error">
              <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <div>
                <h3 className="font-bold">Error Loading Trip</h3>
                <div className="text-xs">{error}</div>
              </div>
            </div>
            <button onClick={() => navigate('/')} className="btn btn-primary gap-2">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                <path strokeLinecap="round" strokeLinejoin="round" d="M10 19l-7-7m0 0l7-7m-7 7h18" />
              </svg>
              Back to Planning
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-base-100 py-8">
      {/* Hero Header */}
      <div className="container mx-auto px-4 mb-8">
        <button onClick={() => navigate('/')} className="btn btn-ghost gap-2 mb-6">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
            <path strokeLinecap="round" strokeLinejoin="round" d="M10 19l-7-7m0 0l7-7m-7 7h18" />
          </svg>
          Back to Planning
        </button>

        <div className="hero bg-gradient-to-r from-primary to-secondary text-primary-content rounded-3xl shadow-2xl mb-8">
          <div className="hero-content py-12">
            <div className="flex flex-col lg:flex-row items-center justify-between w-full gap-6">
              <div className="text-center lg:text-left">
                <h1 className="mb-4 text-4xl font-bold md:text-6xl">{trip.destination}</h1>
                <div className="flex flex-wrap gap-2 justify-center lg:justify-start mb-4">
                  <div className="badge badge-lg badge-outline badge-primary">
                     {trip.startDate} - {trip.endDate}
                  </div>
                  <div className="badge badge-lg badge-outline badge-secondary">
                     {trip.theme}
                  </div>
                  <div className="badge badge-lg badge-outline">
                     {trip.groupSize} people
                  </div>
                </div>
              </div>

              <div className="flex flex-wrap gap-2 justify-center lg:justify-end">
                <button
                  onClick={handleSaveTrip}
                  className={`btn gap-2 ${saved ? 'btn-success' : 'btn-ghost bg-base-100/20 hover:bg-base-100/30'}`}
                >
                  {saved ? (
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-5 h-5">
                      <path d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
                    </svg>
                  ) : (
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                      <path strokeLinecap="round" strokeLinejoin="round" d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
                    </svg>
                  )}
                  {saved ? 'Saved' : 'Save'}
                </button>

                <div className="dropdown dropdown-end">
                  <div tabIndex={0} role="button" className="btn btn-ghost bg-base-100/20 hover:bg-base-100/30">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                      <path strokeLinecap="round" strokeLinejoin="round" d="M12 10v6m0 0l4-4m-4 4l-4-4m-6 2a9 9 0 110-18 9 9 0 010 18z" />
                    </svg>
                  </div>
                  <ul tabIndex={0} className="dropdown-content menu bg-base-100 rounded-box z-[1] w-52 p-2 shadow-lg border border-base-300">
                    <li>
                      <button onClick={handleExportPdf} className="gap-2">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M19.5 14.25v-2.625a3.375 3.375 0 00-3.375-3.375h-1.5A1.125 1.125 0 0113.5 7.125v-1.5a3.375 3.375 0 00-3.375-3.375H8.25m0 12.75h7.5m-7.5 3H12M10.5 2.25H5.625c-.621 0-1.125.504-1.125 1.125v17.25c0 .621.504 1.125 1.125 1.125h12.75c.621 0 1.125-.504 1.125-1.125V11.25a9 9 0 00-9-9z" />
                        </svg>
                        Export as PDF
                      </button>
                    </li>
                    <li>
                      <button onClick={handleExportExcel} className="gap-2">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M3 13.125C3 12.504 3.504 12 4.125 12h2.25c.621 0 1.125.504 1.125 1.125v6.75C7.5 20.496 6.996 21 6.375 21h-2.25A1.125 1.125 0 013 19.875v-6.75zM9.75 8.625c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125v11.25c0 .621-.504 1.125-1.125 1.125h-2.25a1.125 1.125 0 01-1.125-1.125V8.625zM16.5 4.125c0-.621.504-1.125 1.125-1.125h2.25C20.496 3 21 3.504 21 4.125v15.75c0 .621-.504 1.125-1.125 1.125h-2.25a1.125 1.125 0 01-1.125-1.125V4.125z" />
                        </svg>
                        Export as Excel
                      </button>
                    </li>
                  </ul>
                </div>

                <button onClick={handleBookNow} className="btn btn-primary gap-2">
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                  </svg>
                  Book Now
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container mx-auto px-4">
        {/* Tabs */}
        <div className="tabs tabs-boxed justify-center mb-8">
          <button
            className={`tab tab-lg ${activeTab === 'overview' ? 'tab-active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5 mr-2">
              <path strokeLinecap="round" strokeLinejoin="round" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
            </svg>
            Overview
          </button>
          <button
            className={`tab tab-lg ${activeTab === 'itinerary' ? 'tab-active' : ''}`}
            onClick={() => setActiveTab('itinerary')}
          >
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5 mr-2">
              <path strokeLinecap="round" strokeLinejoin="round" d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
            </svg>
            Itinerary
          </button>
          <button
            className={`tab tab-lg ${activeTab === 'map' ? 'tab-active' : ''}`}
            onClick={() => setActiveTab('map')}
          >
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5 mr-2">
              <path strokeLinecap="round" strokeLinejoin="round" d="M9 20l-5.447-2.724A1 1 0 013 16.382V5.618a1 1 0 011.447-.894L9 7m0 13l6-3m-6 3V7m6 10l4.553 2.276A1 1 0 0021 18.382V7.618a1 1 0 00-.553-.894L15 4m0 13V4m0 0L9 7" />
            </svg>
            Map
          </button>
        </div>

        {/* Tab Content */}
        {activeTab === 'overview' && (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Transportation */}
            <div className="card bg-base-100 shadow-xl">
              <div className="card-body">
                <div className="flex items-center gap-4 mb-4">
                  <div className="avatar placeholder">
                    <div className="bg-info text-info-content rounded-xl w-16">
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-8 h-8">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                      </svg>
                    </div>
                  </div>
                  <div>
                    <h3 className="card-title text-2xl">Transportation</h3>
                    <p className="text-base-content/60">Your journey details</p>
                  </div>
                </div>

                {trip.transportation ? (
                  <div className="space-y-3">
                    <div className="flex justify-between">
                      <span className="text-base-content/70">Type & Provider</span>
                      <span className="font-semibold">{trip.transportation.type}  {trip.transportation.provider}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-base-content/70">Route</span>
                      <span className="font-semibold">{trip.transportation.origin}  {trip.transportation.destination}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-base-content/70">Departure</span>
                      <span className="font-semibold">{trip.transportation.departureDate}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-base-content/70">Return</span>
                      <span className="font-semibold">{trip.transportation.returnDate}</span>
                    </div>
                    <div className="divider my-2"></div>
                    <div className="flex justify-between items-center text-lg">
                      <span className="font-semibold">Total Cost</span>
                      <span className="text-xl font-bold text-primary">{trip.transportation.cost} {trip.currency}</span>
                    </div>
                  </div>
                ) : (
                  <p className="text-center text-base-content/50 py-8">Transportation details not available</p>
                )}
              </div>
            </div>

            {/* Accommodation */}
            <div className="card bg-base-100 shadow-xl">
              <div className="card-body">
                <div className="flex items-center gap-4 mb-4">
                  <div className="avatar placeholder">
                    <div className="bg-success text-success-content rounded-xl w-16">
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-8 h-8">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                      </svg>
                    </div>
                  </div>
                  <div>
                    <h3 className="card-title text-2xl">Accommodation</h3>
                    <p className="text-base-content/60">Where you'll stay</p>
                  </div>
                </div>

                {trip.accommodation ? (
                  <div className="space-y-3">
                    <div>
                      <h4 className="text-xl font-bold mb-2">{trip.accommodation.name}</h4>
                      <div className="flex gap-2 items-center mb-3">
                        <div className="badge badge-outline">{trip.accommodation.type}</div>
                        <div className="badge badge-secondary gap-1">
                          <span></span>
                          <span>{trip.accommodation.rating}</span>
                        </div>
                      </div>
                    </div>

                    <div className="space-y-2 text-sm">
                      <div className="flex justify-between">
                        <span className="text-base-content/70">Location</span>
                        <span className="font-semibold">{trip.accommodation.location}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-base-content/70">Check-in</span>
                        <span className="font-semibold">{trip.accommodation.checkInDate}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-base-content/70">Check-out</span>
                        <span className="font-semibold">{trip.accommodation.checkOutDate}</span>
                      </div>
                    </div>

                    <div className="divider my-2"></div>
                    <div className="flex justify-between items-center text-lg">
                      <span className="font-semibold">Total Cost</span>
                      <span className="text-xl font-bold text-primary">{trip.accommodation.cost} {trip.currency}</span>
                    </div>
                  </div>
                ) : (
                  <p className="text-center text-base-content/50 py-8">Accommodation details not available</p>
                )}
              </div>
            </div>

            {/* Budget Breakdown */}
            <div className="card bg-base-100 shadow-xl lg:col-span-2">
              <div className="card-body">
                <h3 className="card-title text-3xl mb-6">Budget Breakdown</h3>

                {trip.budgetBreakdown ? (
                  <div className="stats stats-vertical lg:stats-horizontal shadow w-full">
                    <div className="stat">
                      <div className="stat-figure text-info">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-8 h-8">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                        </svg>
                      </div>
                      <div className="stat-title">Transportation</div>
                      <div className="stat-value text-info">{trip.budgetBreakdown.transportationCost}</div>
                      <div className="stat-desc">{trip.currency}</div>
                    </div>

                    <div className="stat">
                      <div className="stat-figure text-success">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-8 h-8">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                        </svg>
                      </div>
                      <div className="stat-title">Accommodation</div>
                      <div className="stat-value text-success">{trip.budgetBreakdown.accommodationCost}</div>
                      <div className="stat-desc">{trip.currency}</div>
                    </div>

                    <div className="stat">
                      <div className="stat-figure text-secondary">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-8 h-8">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M14.828 14.828a4 4 0 01-5.656 0M9 10h1m4 0h1m-6 8a9 9 0 110-18 9 9 0 010 18z" />
                        </svg>
                      </div>
                      <div className="stat-title">Activities</div>
                      <div className="stat-value text-secondary">{trip.budgetBreakdown.activitiesCost}</div>
                      <div className="stat-desc">{trip.currency}</div>
                    </div>

                    <div className="stat">
                      <div className="stat-figure text-primary">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-8 h-8">
                          <path strokeLinecap="round" strokeLinejoin="round" d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
                        </svg>
                      </div>
                      <div className="stat-title">Total Budget</div>
                      <div className="stat-value text-primary">{trip.budgetBreakdown.totalCost}</div>
                      <div className="stat-desc">{trip.currency}</div>
                    </div>
                  </div>
                ) : (
                  <p className="text-center text-base-content/50 py-8">Budget breakdown not available</p>
                )}
              </div>
            </div>
          </div>
        )}

        {activeTab === 'itinerary' && (
          <div className="card bg-base-100 shadow-xl">
            <div className="card-body">
              <h3 className="card-title text-3xl mb-6">Daily Itinerary</h3>

              {trip.dailyItineraries && trip.dailyItineraries.length > 0 ? (
                <div className="space-y-6">
                  {trip.dailyItineraries.map((day, index) => (
                    <div key={index} className="border-l-4 border-primary pl-6">
                      <div className="flex gap-4 items-center mb-4">
                        <div className="badge badge-primary badge-lg">Day {index + 1}</div>
                        <h4 className="text-xl font-bold">{day.date}</h4>
                      </div>

                      {day.activities && day.activities.map((activity, actIndex) => (
                        <div key={actIndex} className="card bg-base-200 mb-4">
                          <div className="card-body">
                            <div className="flex flex-col md:flex-row justify-between items-start gap-4">
                              <div className="flex-1">
                                <div className="flex gap-2 items-center mb-2">
                                  <span className="badge badge-primary">{activity.time}</span>
                                  <h5 className="text-lg font-bold">{activity.name}</h5>
                                </div>
                                <p className="text-base-content/70 mb-2">{activity.description}</p>
                                <div className="badge badge-outline badge-primary">
                                  {activity.cost} {trip.currency}
                                </div>
                              </div>
                              <div className="badge badge-secondary badge-lg">{activity.type}</div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-12">
                  <svg className="mx-auto mb-4 w-16 h-16 text-base-content/30" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                  </svg>
                  <p className="text-lg text-base-content/50">No itinerary available for this trip.</p>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'map' && (
          <div className="card bg-base-100 shadow-xl">
            <div className="card-body text-center py-16">
              <div className="text-8xl mb-6"></div>
              <h3 className="card-title text-4xl justify-center mb-4">Interactive Map</h3>
              <p className="text-base-content/60 max-w-2xl mx-auto">
                Map visualization will be displayed here showing your trip route and points of interest.
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TripResults;
