import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const SavedTrips = () => {
  const navigate = useNavigate();
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [deleteDialog, setDeleteDialog] = useState({ open: false, tripId: null });

  useEffect(() => {
    fetchSavedTrips();
  }, []);

  const fetchSavedTrips = async () => {
    try {
      const response = await axios.get('/api/trips');
      setTrips(response.data);
    } catch (err) {
      setError('Failed to load saved trips');
      console.error('Error fetching trips:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleViewTrip = (tripId) => {
    navigate(`/results/${tripId}`);
  };

  const handleBookTrip = (tripId) => {
    navigate(`/booking/${tripId}`);
  };

  const handleDeleteTrip = async (tripId) => {
    try {
      await axios.delete(`/api/trips/${tripId}`);
      setTrips(trips.filter(trip => trip.id !== tripId));
      setDeleteDialog({ open: false, tripId: null });
    } catch (err) {
      setError('Failed to delete trip');
      console.error('Error deleting trip:', err);
    }
  };

  const handleExportPdf = async (tripId) => {
    try {
      const response = await axios.get(`/api/export/pdf/${tripId}`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `trip-plan-${tripId}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Error exporting PDF:', err);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-base-100">
        <div className="card bg-base-100 shadow-xl max-w-md">
          <div className="card-body text-center">
            <span className="loading loading-spinner loading-lg text-primary"></span>
            <h2 className="card-title justify-center text-2xl">Loading your saved trips...</h2>
            <p className="text-base-content/60">Fetching your vacation memories</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-base-100">
        <div className="card bg-base-100 shadow-xl max-w-lg">
          <div className="card-body">
            <div className="alert alert-error">
              <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <div>
                <h3 className="font-bold">Error Loading Trips</h3>
                <div className="text-xs">{error}</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (trips.length === 0) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-base-100">
        <div className="hero bg-base-200 rounded-3xl shadow-xl max-w-2xl mx-4">
          <div className="hero-content text-center py-16">
            <div className="max-w-xl">
              <div className="text-8xl mb-6">‚úàÔ∏è</div>
              <h1 className="mb-6 text-5xl font-bold">No Saved Trips Yet</h1>
              <p className="mb-8 text-xl text-base-content/70">
                Start planning your first vacation to see it here! Your dream destinations are waiting.
              </p>
              <button onClick={() => navigate('/')} className="btn btn-primary btn-lg gap-2">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-6 h-6">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
                </svg>
                Plan Your First Trip
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-base-100 py-8">
      {/* Hero Section */}
      <div className="hero bg-gradient-to-r from-primary to-secondary text-primary-content rounded-3xl shadow-2xl mx-4 mb-8">
        <div className="hero-content py-12 text-center">
          <div className="max-w-4xl">
            <div className="text-7xl mb-6">üìö</div>
            <h1 className="mb-6 text-5xl font-bold md:text-6xl">Your Saved Trips</h1>
            <p className="text-xl opacity-90">
              All your vacation plans in one beautiful collection
            </p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container mx-auto px-4">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {trips.map((trip, index) => (
            <div key={trip.id} className="card bg-base-100 shadow-xl hover:shadow-2xl transition-shadow">
              <div className="card-body">
                {/* Card Header */}
                <div className="flex justify-between items-start mb-4">
                  <div className="flex-1">
                    <h2 className="card-title text-2xl mb-3">{trip.destination}</h2>
                    <div className="flex flex-wrap gap-2 mb-4">
                      <div className="badge badge-outline badge-primary">
                        üìÖ {trip.startDate} - {trip.endDate}
                      </div>
                      <div className="badge badge-outline badge-secondary">
                        ‚ù§Ô∏è {trip.theme}
                      </div>
                      <div className="badge badge-outline">
                        üë• {trip.groupSize} people
                      </div>
                    </div>
                  </div>
                  <div className="dropdown dropdown-end">
                    <div tabIndex={0} role="button" className="btn btn-ghost btn-sm btn-circle">
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
                      </svg>
                    </div>
                    <ul tabIndex={0} className="dropdown-content menu bg-base-100 rounded-box z-[1] w-52 p-2 shadow-lg border border-base-300">
                      <li>
                        <button onClick={() => handleExportPdf(trip.id)} className="gap-2">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M19.5 14.25v-2.625a3.375 3.375 0 00-3.375-3.375h-1.5A1.125 1.125 0 0113.5 7.125v-1.5a3.375 3.375 0 00-3.375-3.375H8.25m0 12.75h7.5m-7.5 3H12M10.5 2.25H5.625c-.621 0-1.125.504-1.125 1.125v17.25c0 .621.504 1.125 1.125 1.125h12.75c.621 0 1.125-.504 1.125-1.125V11.25a9 9 0 00-9-9z" />
                          </svg>
                          Export as PDF
                        </button>
                      </li>
                      <li>
                        <button onClick={() => setDeleteDialog({ open: true, tripId: trip.id })} className="text-error gap-2">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M14.74 9l-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 01-2.244 2.077H8.084a2.25 2.25 0 01-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 00-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 013.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 00-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 00-7.5 0" />
                          </svg>
                          Delete Trip
                        </button>
                      </li>
                    </ul>
                  </div>
                </div>

                {/* Trip Details */}
                <div className="space-y-3 mb-4">
                  {trip.transportation && (
                    <div className="flex gap-3 items-center p-3 bg-info/10 rounded-lg">
                      <div className="avatar placeholder">
                        <div className="bg-info text-info-content rounded-lg w-12">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-6 h-6">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                          </svg>
                        </div>
                      </div>
                      <div>
                        <div className="font-semibold">{trip.transportation.type}</div>
                        <div className="text-sm text-base-content/60">via {trip.transportation.provider}</div>
                      </div>
                    </div>
                  )}

                  {trip.accommodation && (
                    <div className="flex gap-3 items-center p-3 bg-success/10 rounded-lg">
                      <div className="avatar placeholder">
                        <div className="bg-success text-success-content rounded-lg w-12">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-6 h-6">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                          </svg>
                        </div>
                      </div>
                      <div>
                        <div className="font-semibold">{trip.accommodation.name}</div>
                        <div className="text-sm text-base-content/60">{trip.accommodation.type}</div>
                      </div>
                    </div>
                  )}
                </div>

                {/* Card Footer */}
                <div className="card-actions justify-between items-center pt-4 border-t border-base-300">
                  <div>
                    <div className="text-sm text-base-content/60">Total Cost</div>
                    <div className="text-2xl font-bold text-primary">
                      {trip.budgetBreakdown?.totalCost} {trip.currency}
                    </div>
                  </div>
                  <div className="flex gap-2">
                    <button
                      onClick={() => handleViewTrip(trip.id)}
                      className="btn btn-outline btn-sm gap-2"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                        <path strokeLinecap="round" strokeLinejoin="round" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                      </svg>
                      View
                    </button>
                    <button
                      onClick={() => handleBookTrip(trip.id)}
                      className="btn btn-primary btn-sm gap-2"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                      </svg>
                      Book
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      <dialog className={`modal ${deleteDialog.open ? 'modal-open' : ''}`}>
        <div className="modal-box">
          <h3 className="font-bold text-lg text-error mb-4 flex items-center gap-2">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-6 h-6">
              <path strokeLinecap="round" strokeLinejoin="round" d="M14.74 9l-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 01-2.244 2.077H8.084a2.25 2.25 0 01-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 00-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 013.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 00-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 00-7.5 0" />
            </svg>
            Delete Trip
          </h3>
          <p className="py-4">
            Are you sure you want to delete this trip? This action cannot be undone.
          </p>
          <div className="modal-action">
            <button
              onClick={() => setDeleteDialog({ open: false, tripId: null })}
              className="btn btn-ghost"
            >
              Cancel
            </button>
            <button
              onClick={() => handleDeleteTrip(deleteDialog.tripId)}
              className="btn btn-error gap-2"
            >
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                <path strokeLinecap="round" strokeLinejoin="round" d="M14.74 9l-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 01-2.244 2.077H8.084a2.25 2.25 0 01-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 00-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 013.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 00-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 00-7.5 0" />
              </svg>
              Delete
            </button>
          </div>
        </div>
        <form method="dialog" className="modal-backdrop">
          <button onClick={() => setDeleteDialog({ open: false, tripId: null })}>close</button>
        </form>
      </dialog>
    </div>
  );
};

export default SavedTrips;
