import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import ThemeToggle from './ThemeToggle';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  return (
    <div className="navbar bg-gradient-to-r from-primary to-secondary text-primary-content shadow-lg sticky top-0 z-50 glass-effect">
      <div className="navbar-start">
        <div className="flex items-center gap-3">
          <div className="avatar animate-bounce-subtle">
            <div className="w-10 rounded-full bg-white/20 flex items-center justify-center">
              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
              </svg>
            </div>
          </div>
          <div>
            <h1 className="text-2xl font-bold bg-gradient-to-r from-white to-gray-200 bg-clip-text text-transparent">
              Vacation Planner
            </h1>
            <p className="text-xs opacity-80">AI-Powered Trip Planning</p>
          </div>
        </div>
      </div>

      <div className="navbar-center">
        <div className="flex gap-2">
          <button
            onClick={() => navigate('/')}
            className={`btn btn-sm btn-ghost px-6 ${
              isActive('/')
                ? 'bg-white/20 text-white'
                : 'text-white/80 hover:text-white hover:bg-white/10'
            } transition-all duration-200`}
          >
            <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
            </svg>
            Plan Trip
          </button>
          <button
            onClick={() => navigate('/saved')}
            className={`btn btn-sm btn-ghost px-6 ${
              isActive('/saved')
                ? 'bg-white/20 text-white'
                : 'text-white/80 hover:text-white hover:bg-white/10'
            } transition-all duration-200`}
          >
            <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
            </svg>
            Saved Trips
          </button>
        </div>
      </div>

      <div className="navbar-end">
        <div className="flex items-center gap-3">
          <div className="hidden md:flex items-center gap-2 text-white/80">
            <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
            <span className="text-sm">AI Ready</span>
          </div>
          <ThemeToggle />
        </div>
      </div>
    </div>
  );
};

export default Navbar;
