import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const SavedTrips = () => {
  const navigate = useNavigate();
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [deleteDialog, setDeleteDialog] = useState({ open: false, tripId: null });

  useEffect(() => {
    fetchSavedTrips();
  }, []);

  const fetchSavedTrips = async () => {
    try {
      const response = await axios.get('/api/trips');
      setTrips(response.data);
    } catch (err) {
      setError('Failed to load saved trips');
      console.error('Error fetching trips:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleViewTrip = (tripId) => {
    navigate(`/results/${tripId}`);
  };

  const handleBookTrip = (tripId) => {
    navigate(`/booking/${tripId}`);
  };

  const handleDeleteTrip = async (tripId) => {
    try {
      await axios.delete(`/api/trips/${tripId}`);
      setTrips(trips.filter(trip => trip.id !== tripId));
      setDeleteDialog({ open: false, tripId: null });
    } catch (err) {
      setError('Failed to delete trip');
      console.error('Error deleting trip:', err);
    }
  };

  const handleExportPdf = async (tripId) => {
    try {
      const response = await axios.get(`/api/export/pdf/${tripId}`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `trip-plan-${tripId}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Error exporting PDF:', err);
    }
  };

  if (loading) {
    return (
      <div className="flex relative justify-center items-center min-h-screen gradient-bg">
        <div className="absolute inset-0 opacity-30 bg-mesh"></div>
        <div className="relative p-12 mx-4 max-w-md text-center animate-fade-in glass-card">
          <div className="mx-auto mb-6 w-16 h-16 loading-modern"></div>
          <h2 className="mb-3 text-3xl font-bold text-gray-800">
            Loading your saved trips...
          </h2>
          <p className="text-lg text-gray-600">Fetching your vacation memories</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-br from-primary/5 via-secondary/5 to-accent/5">
        <div className="mx-auto max-w-md text-center animate-fade-in">
          <div className="shadow-lg alert alert-error">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span>{error}</span>
          </div>
        </div>
      </div>
    );
  }

  if (trips.length === 0) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-br from-primary/5 via-secondary/5 to-accent/5">
        <div className="px-4 mx-auto max-w-2xl text-center animate-fade-in">
          <div className="mb-8 text-8xl animate-bounce-subtle">‚úàÔ∏è</div>
          <h2 className="mb-4 text-4xl font-bold text-base-content">No Saved Trips Yet</h2>
          <p className="mb-8 text-xl text-base-content/70">
            Start planning your first vacation to see it here! Your dream destinations are waiting.
          </p>
          <button
            onClick={() => navigate('/')}
            className="text-lg font-semibold btn-gradient btn-lg"
          >
            <svg className="mr-2 w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
            </svg>
            Plan Your First Trip
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="relative min-h-screen gradient-bg">
      <div className="absolute inset-0 opacity-20 bg-mesh"></div>

      {/* Modern Hero Section */}
      <div className="relative py-16">
        <div className="container px-4 mx-auto max-w-4xl text-center">
          <div className="animate-fade-in">
            <div className="mb-6 text-7xl animate-bounce-subtle">üìö</div>
            <h1 className="mb-6 text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600 md:text-6xl">
              Your Saved Trips
            </h1>
            <p className="mx-auto max-w-2xl text-xl text-gray-600">
              All your vacation plans in one beautiful collection
            </p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container relative px-4 py-12 mx-auto max-w-7xl">
        <div className="grid grid-cols-1 gap-8 md:grid-cols-2 lg:grid-cols-3">
          {trips.map((trip, index) => (
            <div
              key={trip.id}
              className="glass-card p-8 animate-fade-in hover:scale-[1.02] transition-all duration-300"
              style={{animationDelay: `${index * 0.1}s`}}
            >
              {/* Card Header */}
              <div className="mb-6">
                <div className="flex justify-between items-start mb-4">
                  <div className="flex-1">
                    <h3 className="mb-3 text-2xl font-bold text-gray-800">{trip.destination}</h3>
                    <div className="flex flex-wrap gap-2">
                      <span className="px-3 py-1 text-sm font-medium text-blue-700 bg-blue-100 rounded-full">
                        {trip.startDate} - {trip.endDate}
                      </span>
                      <span className="px-3 py-1 text-sm font-medium text-purple-700 bg-purple-100 rounded-full">
                        {trip.theme}
                      </span>
                      <span className="px-3 py-1 text-sm font-medium text-gray-700 bg-gray-100 rounded-full">
                        {trip.groupSize} people
                      </span>
                    </div>
                  </div>
                  <div className="dropdown dropdown-end">
                    <button className="p-2 rounded-lg transition-colors hover:bg-gray-100">
                      <svg className="w-5 h-5 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
                      </svg>
                    </button>
                    <ul className="p-2 w-52 bg-white rounded-xl border border-gray-200 shadow dropdown-content menu">
                      <li><button onClick={() => handleExportPdf(trip.id)} className="text-gray-700 hover:bg-gray-50">Export as PDF</button></li>
                      <li><button onClick={() => setDeleteDialog({ open: true, tripId: trip.id })} className="text-red-600 hover:bg-red-50">Delete Trip</button></li>
                    </ul>
                  </div>
                </div>

                {/* Trip Details */}
                <div className="space-y-3">
                  {trip.transportation && (
                    <div className="flex gap-3 items-center p-3 rounded-lg bg-blue-500/10">
                      <div className="p-2 rounded-lg bg-blue-500/20">
                        <svg className="w-5 h-5 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                        </svg>
                      </div>
                      <div>
                        <div className="font-semibold text-base-content">{trip.transportation.type}</div>
                        <div className="text-sm text-base-content/70">via {trip.transportation.provider}</div>
                      </div>
                    </div>
                  )}

                  {trip.accommodation && (
                    <div className="flex gap-3 items-center p-3 rounded-lg bg-green-500/10">
                      <div className="p-2 rounded-lg bg-green-500/20">
                        <svg className="w-5 h-5 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                        </svg>
                      </div>
                      <div>
                        <div className="font-semibold text-base-content">{trip.accommodation.name}</div>
                        <div className="text-sm text-base-content/70">{trip.accommodation.type}</div>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Card Footer */}
              <div className="p-6">
                <div className="flex justify-between items-center mb-4">
                  <span className="text-sm text-base-content/70">Total Cost</span>
                  <span className="text-2xl font-bold text-primary">
                    {trip.budgetBreakdown?.totalCost} {trip.currency}
                  </span>
                </div>

                <div className="flex gap-2">
                  <button
                    onClick={() => handleViewTrip(trip.id)}
                    className="flex-1 btn btn-outline btn-sm"
                  >
                    <svg className="mr-2 w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                    </svg>
                    View Details
                  </button>
                  <button
                    onClick={() => handleBookTrip(trip.id)}
                    className="flex-1 btn-gradient btn-sm"
                  >
                    <svg className="mr-2 w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                    </svg>
                    Book Now
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      {deleteDialog.open && (
        <div className="flex fixed inset-0 z-50 justify-center items-center bg-black/50 animate-fade-in">
          <div className="modal modal-open">
            <div className="modal-box">
              <h3 className="mb-4 text-lg font-bold text-error">
                <svg className="inline mr-2 w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                </svg>
                Delete Trip
              </h3>
              <p className="py-4">
                Are you sure you want to delete this trip? This action cannot be undone.
              </p>
              <div className="modal-action">
                <button
                  onClick={() => setDeleteDialog({ open: false, tripId: null })}
                  className="btn btn-ghost"
                >
                  Cancel
                </button>
                <button
                  onClick={() => handleDeleteTrip(deleteDialog.tripId)}
                  className="btn btn-error"
                >
                  <svg className="mr-2 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                  </svg>
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SavedTrips;
