import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const TripPlanner = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    budget: '',
    currency: 'USD',
    startDate: '',
    endDate: '',
    theme: '',
    groupSize: '',
    startingPoint: '',
  });

  const themes = [
    'Adventure', 'Relaxation', 'Cultural', 'Food & Wine',
    'Beach', 'Mountain', 'City Break', 'Romantic'
  ];

  const groupSizes = ['1', '2', '3-4', '5-8', '9+'];
  const currencies = ['USD', 'EUR', 'GBP', 'JPY', 'CAD', 'AUD'];

  const handleInputChange = (field) => (event) => {
    setFormData({
      ...formData,
      [field]: event.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Convert data types to match backend expectations
      const requestData = {
        budget: parseFloat(formData.budget) || 0,
        currency: formData.currency,
        startDate: formData.startDate,
        endDate: formData.endDate,
        theme: formData.theme,
        groupSize: parseInt(formData.groupSize) || 1,
        startingPoint: formData.startingPoint,
      };

      const response = await axios.post('/api/trips/plan', requestData);
      navigate(`/results/${response.data.id}`);
    } catch (err) {
      setError('Failed to plan trip. Please try again.');
      console.error('Error planning trip:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setFormData({
      budget: '',
      currency: 'USD',
      startDate: '',
      endDate: '',
      theme: '',
      groupSize: '',
      startingPoint: '',
    });
    setError('');
  };

  return (
    <div className="relative min-h-screen gradient-bg">
      <div className="absolute inset-0 opacity-20 bg-mesh"></div>

      {/* Modern Hero Section */}
      <div className="relative py-20 hero-modern">
        <div className="max-w-6xl text-center hero-content">
          <div className="animate-fade-in">
            <div className="mb-12">
              <div className="relative mb-8">
                <div className="mb-6 text-8xl animate-bounce-subtle">⭐</div>
                <div className="absolute inset-0 text-8xl opacity-20 animate-pulse">✨</div>
              </div>

              <h1 className="mb-8 text-6xl font-bold leading-tight text-transparent bg-clip-text bg-gradient-to-r from-white via-blue-100 to-purple-200 md:text-8xl">
                Plan Your Perfect
                <span className="block text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 via-orange-400 to-red-400">
                  Vacation
                </span>
              </h1>

              <p className="mx-auto mb-12 max-w-4xl text-2xl leading-relaxed md:text-3xl text-white/90">
                Let AI create the perfect trip tailored to your preferences
              </p>

              <div className="flex flex-col gap-6 justify-center items-center text-lg md:flex-row md:gap-8">
                <div className="flex gap-3 items-center px-6 py-3 glass-card">
                  <div className="w-4 h-4 bg-green-400 rounded-full animate-pulse-glow"></div>
                  <span className="font-semibold text-white">AI-Powered Planning</span>
                </div>
                <div className="flex gap-3 items-center px-6 py-3 glass-card">
                  <div className="w-4 h-4 bg-blue-400 rounded-full animate-pulse-glow"></div>
                  <span className="font-semibold text-white">Real-time Updates</span>
                </div>
                <div className="flex gap-3 items-center px-6 py-3 glass-card">
                  <div className="w-4 h-4 bg-purple-400 rounded-full animate-pulse-glow"></div>
                  <span className="font-semibold text-white">Smart Recommendations</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Form */}
      <div className="container relative px-4 py-16 mx-auto max-w-7xl">
        {error && (
          <div className="mx-auto mb-12 max-w-2xl bg-red-50 alert-modern border-red-200/50 animate-slide-in">
            <div className="flex justify-center items-center mx-auto mb-4 w-12 h-12 bg-red-100 rounded-full">
              <svg className="w-6 h-6 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h3 className="mb-2 text-xl font-bold text-red-800">Error Planning Trip</h3>
            <p className="text-red-600">{error}</p>
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-12">
          {/* Budget Section */}
          <div className="p-10 mx-auto max-w-5xl glass-card animate-fade-in">
            <div className="flex gap-6 items-center mb-8">
              <div className="p-4 bg-gradient-to-br from-green-100 to-emerald-100 rounded-2xl border border-green-200">
                <svg className="w-10 h-10 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
                </svg>
              </div>
              <div>
                <h2 className="mb-2 text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-600 to-emerald-600">
                  Budget Planning
                </h2>
                <p className="text-lg text-gray-600">Set your travel budget and preferences</p>
              </div>
            </div>

            <div className="grid grid-cols-1 gap-8 md:grid-cols-2">
              <div className="relative">
                <input
                  type="number"
                  placeholder="Enter your budget (e.g., 5000)"
                  className="text-lg input-modern"
                  value={formData.budget}
                  onChange={handleInputChange('budget')}
                  required
                />
                <label className="text-base font-medium input-label">Total Budget</label>
              </div>

              <div className="relative">
                <select
                  className="text-lg input-modern bg-white/80"
                  value={formData.currency}
                  onChange={handleInputChange('currency')}
                  required
                >
                  {currencies.map((currency) => (
                    <option key={currency} value={currency}>{currency}</option>
                  ))}
                </select>
                <label className="text-base font-medium input-label">Currency</label>
              </div>
            </div>
          </div>

          {/* Travel Dates */}
          <div className="p-10 mx-auto max-w-5xl glass-card animate-fade-in" style={{animationDelay: '0.1s'}}>
            <div className="flex gap-6 items-center mb-8">
              <div className="p-4 bg-gradient-to-br from-blue-100 to-cyan-100 rounded-2xl border border-blue-200">
                <svg className="w-10 h-10 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
              </div>
              <div>
                <h2 className="mb-2 text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-cyan-600">
                  Travel Dates
                </h2>
                <p className="text-lg text-gray-600">When would you like to travel?</p>
              </div>
            </div>

            <div className="grid grid-cols-1 gap-8 md:grid-cols-2">
              <div className="relative">
                <input
                  type="date"
                  className="text-lg input-modern bg-white/80"
                  value={formData.startDate}
                  onChange={handleInputChange('startDate')}
                  required
                />
                <label className="text-base font-medium input-label">Start Date</label>
              </div>

              <div className="relative">
                <input
                  type="date"
                  className="text-lg input-modern bg-white/80"
                  value={formData.endDate}
                  onChange={handleInputChange('endDate')}
                  required
                />
                <label className="text-base font-medium input-label">End Date</label>
              </div>
            </div>
          </div>

          {/* Preferences */}
          <div className="p-10 mx-auto max-w-5xl glass-card animate-fade-in" style={{animationDelay: '0.2s'}}>
            <div className="flex gap-6 items-center mb-8">
              <div className="p-4 bg-gradient-to-br from-purple-100 to-pink-100 rounded-2xl border border-purple-200">
                <svg className="w-10 h-10 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                </svg>
              </div>
              <div>
                <h2 className="mb-2 text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-purple-600 to-pink-600">
                  Travel Preferences
                </h2>
                <p className="text-lg text-gray-600">Tell us about your dream vacation</p>
              </div>
            </div>

            <div className="grid grid-cols-1 gap-8 mb-8 md:grid-cols-2">
              <div className="relative">
                <select
                  className="text-lg input-modern bg-white/80"
                  value={formData.theme}
                  onChange={handleInputChange('theme')}
                  required
                >
                  <option value="">Select a theme</option>
                  {themes.map((theme) => (
                    <option key={theme} value={theme}>{theme}</option>
                  ))}
                </select>
                <label className="text-base font-medium input-label">Vacation Theme</label>
              </div>

              <div className="relative">
                <select
                  className="text-lg input-modern bg-white/80"
                  value={formData.groupSize}
                  onChange={handleInputChange('groupSize')}
                  required
                >
                  <option value="">Select group size</option>
                  {groupSizes.map((size) => (
                    <option key={size} value={size}>{size} {size === '1' ? 'person' : 'people'}</option>
                  ))}
                </select>
                <label className="text-base font-medium input-label">Group Size</label>
              </div>
            </div>

            <div className="relative">
              <input
                type="text"
                placeholder="Enter your home city (e.g., New York, London, Tokyo)"
                className="text-lg input-modern bg-white/80"
                value={formData.startingPoint}
                onChange={handleInputChange('startingPoint')}
                required
              />
              <label className="text-base font-medium input-label">Starting Point</label>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex flex-col gap-4 justify-end items-center sm:flex-row animate-fade-in" style={{animationDelay: '0.3s'}}>
            <button
              type="button"
              onClick={handleClear}
              disabled={loading}
              className="px-8 transition-all duration-200 btn btn-outline btn-error hover:scale-105"
            >
              <svg className="mr-2 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
              </svg>
              Clear Form
            </button>

            <button
              type="submit"
              disabled={loading}
              className="px-12 py-4 text-lg font-semibold shadow-2xl transition-all duration-200 btn-gradient hover:scale-105"
            >
              {loading ? (
                <>
                  <span className="mr-3 loading loading-spinner loading-sm"></span>
                  Planning Your Trip...
                </>
              ) : (
                <>
                  <svg className="mr-3 w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                  </svg>
                  Plan My Perfect Trip
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TripPlanner;
