import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Booking = () => {
  const { tripId } = useParams();
  const navigate = useNavigate();
  const [trip, setTrip] = useState(null);
  const [loading, setLoading] = useState(true);
  const [booking, setBooking] = useState(false);
  const [error, setError] = useState('');
  const [activeStep, setActiveStep] = useState(0);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    cardNumber: '',
  });

  const steps = [
    { id: 'summary', title: 'Trip Summary', icon: 'ðŸ“‹' },
    { id: 'details', title: 'Traveler Details', icon: 'ðŸ‘¤' },
    { id: 'payment', title: 'Payment', icon: 'ðŸ’³' },
    { id: 'confirmation', title: 'Confirmation', icon: 'âœ…' }
  ];

  useEffect(() => {
    const fetchTrip = async () => {
      try {
        const response = await axios.get(`/api/trips/${tripId}`);
        setTrip(response.data);
      } catch (err) {
        setError('Failed to load trip details');
        console.error('Error fetching trip:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTrip();
  }, [tripId]);

  const handleInputChange = (field) => (event) => {
    setFormData({
      ...formData,
      [field]: event.target.value,
    });
  };

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const handleConfirmBooking = async () => {
    setBooking(true);
    setError('');

    try {
      const bookingData = {
        tripId,
        travelerName: formData.name,
        travelerEmail: formData.email,
        travelerPhone: formData.phone,
      };

      const response = await axios.post('/api/booking/book', bookingData);

      if (response.data.success) {
        setActiveStep(3);
      } else {
        setError(response.data.message || 'Booking failed');
      }
    } catch (err) {
      setError('Booking failed. Please try again.');
      console.error('Error booking trip:', err);
    } finally {
      setBooking(false);
    }
  };

  if (loading) {
    return (
      <div className="flex relative justify-center items-center min-h-screen gradient-bg">
        <div className="absolute inset-0 opacity-30 bg-mesh"></div>
        <div className="relative p-12 mx-4 max-w-md text-center animate-fade-in glass-card">
          <div className="mx-auto mb-6 w-16 h-16 loading-modern"></div>
          <h2 className="mb-3 text-3xl font-bold text-transparent text-gray-800 bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
            Loading trip details...
          </h2>
          <p className="text-lg text-gray-600">Preparing your booking experience</p>
        </div>
      </div>
    );
  }

  if (error && !trip) {
    return (
      <div className="flex relative justify-center items-center min-h-screen gradient-bg">
        <div className="absolute inset-0 opacity-30 bg-mesh"></div>
        <div className="relative mx-4 max-w-lg text-center animate-fade-in">
          <div className="mb-8 bg-red-50 alert-modern border-red-200/50">
            <div className="flex justify-center items-center mx-auto mb-4 w-12 h-12 bg-red-100 rounded-full">
              <svg className="w-6 h-6 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h3 className="mb-2 text-xl font-bold text-red-800">Unable to load trip details</h3>
            <p className="text-red-600">{error}</p>
          </div>
          <button
            onClick={() => navigate(`/results/${tripId}`)}
            className="gap-3 px-8 py-4 text-lg btn-gradient"
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
            </svg>
            Back to Results
          </button>
        </div>
      </div>
    );
  }

  const renderStepContent = (step) => {
    switch (step) {
      case 0:
        return (
          <div className="space-y-8 animate-fade-in">
            <div className="mb-12 text-center">
              <h2 className="mb-4 text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
                Trip Summary
              </h2>
              <p className="text-xl text-gray-600">Review your perfect vacation plan</p>
            </div>

            <div className="p-10 mx-auto max-w-4xl card-modern">
              <div className="grid grid-cols-1 gap-8 mb-8 md:grid-cols-2">
                <div className="space-y-6">
                  <div className="p-6 bg-gradient-to-br from-blue-50 to-indigo-50 rounded-2xl border border-blue-100">
                    <div className="mb-2 text-sm font-semibold tracking-wide text-blue-600 uppercase">Destination</div>
                    <div className="text-2xl font-bold text-gray-800">{trip?.destination}</div>
                  </div>
                  <div className="p-6 bg-gradient-to-br from-purple-50 to-pink-50 rounded-2xl border border-purple-100">
                    <div className="mb-2 text-sm font-semibold tracking-wide text-purple-600 uppercase">Travel Dates</div>
                    <div className="text-xl font-semibold text-gray-800">
                      {trip?.startDate} - {trip?.endDate}
                    </div>
                  </div>
                </div>

                <div className="space-y-6">
                  <div className="p-6 bg-gradient-to-br from-green-50 to-emerald-50 rounded-2xl border border-green-100">
                    <div className="mb-2 text-sm font-semibold tracking-wide text-green-600 uppercase">Transportation</div>
                    <div className="text-xl font-semibold text-gray-800">
                      {trip?.transportation?.type} via {trip?.transportation?.provider}
                    </div>
                  </div>
                  <div className="p-6 bg-gradient-to-br from-orange-50 to-amber-50 rounded-2xl border border-orange-100">
                    <div className="mb-2 text-sm font-semibold tracking-wide text-orange-600 uppercase">Accommodation</div>
                    <div className="text-xl font-semibold text-gray-800">
                      {trip?.accommodation?.name} ({trip?.accommodation?.type})
                    </div>
                  </div>
                </div>
              </div>

              <div className="flex justify-center items-center p-8 bg-gradient-to-r from-gray-50 rounded-2xl border-2 border-gray-200 to-slate-50">
                <div className="text-center">
                  <div className="mb-2 text-sm font-semibold tracking-wider text-gray-600 uppercase">Total Cost</div>
                  <div className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
                    {trip?.budgetBreakdown?.totalCost} {trip?.currency}
                  </div>
                </div>
              </div>
            </div>
          </div>
        );

      case 1:
        return (
          <div className="space-y-8 animate-fade-in">
            <div className="mb-12 text-center">
              <div className="mb-6 text-7xl animate-bounce-subtle">ðŸ‘¤</div>
              <h2 className="mb-4 text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
                Traveler Details
              </h2>
              <p className="text-xl text-gray-600">Please provide your contact information</p>
            </div>

            <div className="p-10 mx-auto space-y-8 max-w-3xl glass-card">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Enter your full name"
                  className="text-lg input-modern"
                  value={formData.name}
                  onChange={handleInputChange('name')}
                  required
                />
                <label className="text-base font-medium input-label">Full Name</label>
              </div>

              <div className="relative">
                <input
                  type="email"
                  placeholder="your.email@example.com"
                  className="text-lg input-modern"
                  value={formData.email}
                  onChange={handleInputChange('email')}
                  required
                />
                <label className="text-base font-medium input-label">Email Address</label>
              </div>

              <div className="relative">
                <input
                  type="tel"
                  placeholder="+1 (555) 123-4567"
                  className="text-lg input-modern"
                  value={formData.phone}
                  onChange={handleInputChange('phone')}
                  required
                />
                <label className="text-base font-medium input-label">Phone Number</label>
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="space-y-8 animate-fade-in">
            <div className="mb-12 text-center">
              <div className="mb-6 text-7xl animate-pulse-glow">ðŸ’³</div>
              <h2 className="mb-4 text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
                Payment Information
              </h2>
              <p className="text-xl text-gray-600">Secure payment processing</p>
            </div>

            <div className="mx-auto mb-8 max-w-2xl bg-blue-50 alert-modern border-blue-200/50">
              <div className="flex justify-center items-center mx-auto mb-4 w-12 h-12 bg-blue-100 rounded-full">
                <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <h3 className="mb-2 text-lg font-bold text-blue-800">Demo Mode</h3>
              <p className="text-blue-600">This is a demo application. No real payment will be processed.</p>
            </div>

            <div className="p-10 mx-auto space-y-8 max-w-3xl glass-card">
              <div className="relative">
                <input
                  type="text"
                  placeholder="4111 1111 1111 1111"
                  className="text-lg input-modern"
                  value={formData.cardNumber}
                  onChange={handleInputChange('cardNumber')}
                  required
                />
                <label className="text-base font-medium input-label">Card Number</label>
              </div>

              <div className="grid grid-cols-1 gap-8 md:grid-cols-2">
                <div className="relative">
                  <input
                    type="text"
                    placeholder="MM/YY"
                    className="text-lg input-modern"
                  />
                  <label className="text-base font-medium input-label">Expiry Date</label>
                </div>

                <div className="relative">
                  <input
                    type="text"
                    placeholder="123"
                    className="text-lg input-modern"
                  />
                  <label className="text-base font-medium input-label">CVV</label>
                </div>
              </div>
            </div>
          </div>
        );

      case 3:
        return (
          <div className="py-12 text-center animate-fade-in">
            <div className="mb-8">
              <div className="mb-6 text-8xl animate-bounce-subtle">ðŸŽ‰</div>
              <h2 className="mb-4 text-4xl font-bold text-base-content">Booking Confirmed!</h2>
              <p className="mx-auto mb-8 max-w-2xl text-xl text-base-content/70">
                Your trip has been successfully booked. You will receive a confirmation email shortly with all the details.
              </p>
            </div>

            <div className="flex flex-col gap-4 justify-center sm:flex-row">
              <button
                onClick={() => navigate('/saved')}
                className="text-lg font-semibold btn-gradient btn-lg"
              >
                <svg className="mr-2 w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
                </svg>
                View Saved Trips
              </button>

              <button
                onClick={() => navigate('/')}
                className="btn btn-outline btn-lg"
              >
                <svg className="mr-2 w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
                </svg>
                Plan Another Trip
              </button>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="relative min-h-screen gradient-bg">
      <div className="absolute inset-0 opacity-20 bg-mesh"></div>

      {/* Modern Header */}
      <div className="relative py-12 hero-modern">
        <div className="max-w-5xl text-center hero-content">
          <div className="animate-fade-in">
            <button
              onClick={() => navigate(`/results/${tripId}`)}
              className="gap-3 px-6 py-3 mb-8 rounded-2xl border shadow-lg backdrop-blur-md transition-all duration-300 bg-white/10 border-white/20 text-white/90 hover:text-white hover:bg-white/20 hover:shadow-xl"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
              </svg>
              Back to Results
            </button>

            <h1 className="mb-6 text-5xl font-bold leading-tight text-transparent bg-clip-text bg-gradient-to-r from-white via-blue-100 to-purple-200 md:text-6xl">
              Complete Your Booking
            </h1>
            <p className="mx-auto max-w-2xl text-xl leading-relaxed text-white/90">
              Follow these simple steps to secure your perfect vacation
            </p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container relative px-4 py-12 mx-auto max-w-5xl">
        {/* Modern Progress Steps */}
        <div className="flex justify-center items-center mb-16 space-x-4 md:space-x-8">
          {steps.map((step, index) => (
            <div key={step.id} className="flex items-center">
              <div className={`step-modern ${activeStep >= index ? 'active' : ''} ${activeStep > index ? 'completed' : ''}`}>
                <span className="text-lg">{step.icon}</span>
              </div>
              {index < steps.length - 1 && (
                <div className={`hidden md:block w-16 h-1 mx-4 rounded-full transition-all duration-500
                  ${activeStep > index ? 'bg-gradient-to-r from-purple-500 to-blue-500' : 'bg-gray-200'}`}>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Error Alert */}
        {error && (
          <div className="mb-8 shadow-lg alert alert-error animate-slide-in">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span>{error}</span>
          </div>
        )}

        {/* Step Content */}
        <div className="min-h-[500px]">
          {renderStepContent(activeStep)}
        </div>

        {/* Navigation Buttons */}
        {activeStep < 3 && (
          <div className="flex justify-between items-center pt-8 mt-8 border-t border-base-200 animate-fade-in">
            <button
              onClick={handleBack}
              disabled={activeStep === 0}
              className="btn btn-outline btn-lg"
            >
              <svg className="mr-2 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
              Back
            </button>

            <div className="flex gap-2 items-center text-sm text-base-content/70">
              <span>Step {activeStep + 1} of {steps.length}</span>
              <div className="w-32 h-2 rounded-full bg-base-200">
                <div
                  className="h-2 rounded-full transition-all duration-300 bg-primary"
                  style={{ width: `${((activeStep + 1) / steps.length) * 100}%` }}
                ></div>
              </div>
            </div>

            <button
              onClick={activeStep === 2 ? handleConfirmBooking : handleNext}
              disabled={booking}
              className="btn-gradient btn-lg"
            >
              {booking ? (
                <>
                  <span className="mr-2 loading loading-spinner loading-sm"></span>
                  Processing...
                </>
              ) : activeStep === 2 ? (
                <>
                  <svg className="mr-2 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  Confirm Booking
                </>
              ) : (
                <>
                  Next
                  <svg className="ml-2 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                  </svg>
                </>
              )}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Booking;
