import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const TripResults = () => {
  const { tripId } = useParams();
  const navigate = useNavigate();
  const [trip, setTrip] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('overview');
  const [saved, setSaved] = useState(false);

  const fetchTrip = useCallback(async () => {
    try {
      const response = await axios.get(`/api/trips/${tripId}`);
      setTrip(response.data);
    } catch (err) {
      setError('Failed to load trip details');
      console.error('Error fetching trip:', err);
    } finally {
      setLoading(false);
    }
  }, [tripId]);

  useEffect(() => {
    if (tripId && tripId !== 'null' && tripId !== 'undefined') {
      fetchTrip();
    } else {
      setError('Invalid trip ID');
      setLoading(false);
    }
  }, [fetchTrip, tripId]);

  const handleSaveTrip = async () => {
    try {
      await axios.post(`/api/trips/${tripId}/save`);
      setSaved(true);
    } catch (err) {
      console.error('Error saving trip:', err);
    }
  };

  const handleBookNow = () => {
    navigate(`/booking/${tripId}`);
  };

  const handleExportPdf = async () => {
    try {
      const response = await axios.get(`/api/export/pdf/${tripId}`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `trip-plan-${tripId}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Error exporting PDF:', err);
    }
  };

  const handleExportExcel = async () => {
    try {
      const response = await axios.get(`/api/export/excel/${tripId}`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `trip-plan-${tripId}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Error exporting Excel:', err);
    }
  };

  if (loading) {
    return (
      <div className="flex relative justify-center items-center min-h-screen gradient-bg">
        <div className="absolute inset-0 opacity-30 bg-mesh"></div>
        <div className="relative p-12 mx-4 max-w-md text-center animate-fade-in glass-card">
          <div className="mx-auto mb-6 w-16 h-16 loading-modern"></div>
          <h2 className="mb-3 text-3xl font-bold text-gray-800">
            Loading trip details...
          </h2>
          <p className="text-lg text-gray-600">Preparing your perfect vacation plan</p>
        </div>
      </div>
    );
  }

  if (error || !trip) {
    return (
      <div className="flex relative justify-center items-center min-h-screen gradient-bg">
        <div className="absolute inset-0 opacity-30 bg-mesh"></div>
        <div className="relative mx-4 max-w-lg text-center animate-fade-in">
          <div className="mb-8 bg-red-50 alert-modern border-red-200/50">
            <div className="flex justify-center items-center mx-auto mb-4 w-12 h-12 bg-red-100 rounded-full">
              <svg className="w-6 h-6 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h3 className="mb-2 text-xl font-bold text-red-800">Error Loading Trip</h3>
            <p className="text-red-600">{error}</p>
          </div>
          <button
            onClick={() => navigate('/')}
            className="gap-3 px-8 py-4 text-lg btn-gradient"
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
            </svg>
            Back to Planning
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary/5 via-secondary/5 to-accent/5">
      {/* Hero Header */}
      <div className="py-12 bg-gradient-to-r hero from-primary to-secondary text-primary-content">
        <div className="max-w-6xl text-center hero-content">
          <div className="animate-fade-in">
            <button
              onClick={() => navigate('/')}
              className="gap-2 mb-6 btn btn-ghost btn-sm text-white/80 hover:text-white"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
              </svg>
              Back to Planning
            </button>

            <div className="flex flex-col gap-8 lg:flex-row lg:items-center lg:justify-between">
              <div className="text-center lg:text-left">
                <h1 className="mb-4 text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-white to-gray-200 md:text-6xl">
                  {trip.destination}
                </h1>
                <div className="flex flex-wrap gap-3 justify-center mb-6 lg:justify-start">
                  <div className="gap-2 badge badge-primary badge-lg">
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                    </svg>
                    {trip.startDate} - {trip.endDate}
                  </div>
                  <div className="gap-2 badge badge-secondary badge-lg">
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                    </svg>
                    {trip.theme}
                  </div>
                  <div className="gap-2 badge badge-accent badge-lg">
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
                    </svg>
                    {trip.groupSize} people
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex flex-wrap gap-3 justify-center lg:justify-end">
                <button
                  onClick={handleSaveTrip}
                  className={`btn btn-circle btn-lg ${saved ? 'btn-primary' : 'btn-ghost text-white/80'}`}
                >
                  {saved ? (
                    <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
                    </svg>
                  ) : (
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
                    </svg>
                  )}
                </button>

                <button className="btn btn-circle btn-ghost text-white/80 btn-lg">
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.367 2.684 3 3 0 00-5.367-2.684z" />
                  </svg>
                </button>

                <div className="dropdown dropdown-end">
                  <button className="btn btn-ghost text-white/80 btn-lg">
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 10v6m0 0l4-4m-4 4l-4-4m-6 2a9 9 0 110-18 9 9 0 010 18z" />
                    </svg>
                  </button>
                  <ul className="p-2 w-52 shadow dropdown-content menu bg-base-100 rounded-box">
                    <li><button onClick={handleExportPdf}>Export as PDF</button></li>
                    <li><button onClick={handleExportExcel}>Export as Excel</button></li>
                  </ul>
                </div>

                <button
                  onClick={handleBookNow}
                  className="text-lg font-semibold btn-gradient btn-lg"
                >
                  <svg className="mr-2 w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                  </svg>
                  Book Now
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container px-4 py-8 mx-auto max-w-7xl">
        {/* Tabs */}
        <div className="justify-center mb-8 shadow-lg tabs tabs-boxed bg-base-100 animate-fade-in">
          <button
            onClick={() => setActiveTab('overview')}
            className={`tab tab-lg ${activeTab === 'overview' ? 'tab-active' : ''}`}
          >
            <svg className="mr-2 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
            </svg>
            Overview
          </button>
          <button
            onClick={() => setActiveTab('itinerary')}
            className={`tab tab-lg ${activeTab === 'itinerary' ? 'tab-active' : ''}`}
          >
            <svg className="mr-2 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
            </svg>
            Itinerary
          </button>
          <button
            onClick={() => setActiveTab('map')}
            className={`tab tab-lg ${activeTab === 'map' ? 'tab-active' : ''}`}
          >
            <svg className="mr-2 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 20l-5.447-2.724A1 1 0 013 16.382V5.618a1 1 0 011.447-.894L9 7m0 13l6-3m-6 3V7m6 10l4.553 2.276A1 1 0 0021 18.382V7.618a1 1 0 00-.553-.894L15 4m0 13V4m0 0L9 7" />
            </svg>
            Map
          </button>
        </div>

        {/* Tab Content */}
        {activeTab === 'overview' && (
          <div className="grid grid-cols-1 gap-8 lg:grid-cols-2 animate-fade-in">
            {/* Transportation */}
            <div className="p-8 card-modern">
              <div className="flex gap-4 items-center mb-6">
                <div className="p-4 rounded-xl bg-blue-500/10">
                  <svg className="w-8 h-8 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                  </svg>
                </div>
                <div>
                  <h3 className="text-2xl font-bold text-base-content">Transportation</h3>
                  <p className="text-base-content/70">Your journey details</p>
                </div>
              </div>

              {trip.transportation ? (
                <div className="space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-base-content/70">Type & Provider</span>
                    <span className="font-semibold">{trip.transportation.type}  {trip.transportation.provider}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-base-content/70">Route</span>
                    <span className="font-semibold">{trip.transportation.origin}  {trip.transportation.destination}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-base-content/70">Departure</span>
                    <span className="font-semibold">{trip.transportation.departureDate}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-base-content/70">Return</span>
                    <span className="font-semibold">{trip.transportation.returnDate}</span>
                  </div>
                  <div className="my-4 divider"></div>
                  <div className="flex justify-between items-center text-lg">
                    <span className="font-semibold">Total Cost</span>
                    <span className="text-xl font-bold text-primary">{trip.transportation.cost} {trip.currency}</span>
                  </div>
                </div>
              ) : (
                <p className="py-8 text-center text-base-content/50">Transportation details not available</p>
              )}
            </div>

            {/* Accommodation */}
            <div className="p-8 card-modern">
              <div className="flex gap-4 items-center mb-6">
                <div className="p-4 rounded-xl bg-green-500/10">
                  <svg className="w-8 h-8 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                  </svg>
                </div>
                <div>
                  <h3 className="text-2xl font-bold text-base-content">Accommodation</h3>
                  <p className="text-base-content/70">Where you'll stay</p>
                </div>
              </div>

              {trip.accommodation ? (
                <div className="space-y-4">
                  <div>
                    <h4 className="mb-2 text-xl font-bold text-base-content">{trip.accommodation.name}</h4>
                    <div className="flex gap-2 items-center mb-3">
                      <span className="badge badge-outline">{trip.accommodation.type}</span>
                      <div className="flex gap-1 items-center">
                        <span></span>
                        <span className="font-semibold">{trip.accommodation.rating}</span>
                      </div>
                    </div>
                  </div>

                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-base-content/70">Location</span>
                      <span className="font-semibold">{trip.accommodation.location}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-base-content/70">Check-in</span>
                      <span className="font-semibold">{trip.accommodation.checkInDate}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-base-content/70">Check-out</span>
                      <span className="font-semibold">{trip.accommodation.checkOutDate}</span>
                    </div>
                  </div>

                  <div className="my-4 divider"></div>
                  <div className="flex justify-between items-center text-lg">
                    <span className="font-semibold">Total Cost</span>
                    <span className="text-xl font-bold text-primary">{trip.accommodation.cost} {trip.currency}</span>
                  </div>
                </div>
              ) : (
                <p className="py-8 text-center text-base-content/50">Accommodation details not available</p>
              )}
            </div>

            {/* Budget Breakdown */}
            <div className="p-8 card-modern lg:col-span-2">
              <h3 className="mb-6 text-2xl font-bold text-base-content">Budget Breakdown</h3>

              {trip.budgetBreakdown ? (
                <div className="grid grid-cols-2 gap-6 md:grid-cols-4">
                  <div className="p-4 text-center rounded-xl bg-blue-500/10">
                    <div className="mb-2 text-blue-600">
                      <svg className="mx-auto w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                      </svg>
                    </div>
                    <p className="mb-1 text-sm text-base-content/70">Transportation</p>
                    <p className="text-xl font-bold text-blue-600">{trip.budgetBreakdown.transportationCost} {trip.currency}</p>
                  </div>

                  <div className="p-4 text-center rounded-xl bg-green-500/10">
                    <div className="mb-2 text-green-600">
                      <svg className="mx-auto w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                      </svg>
                    </div>
                    <p className="mb-1 text-sm text-base-content/70">Accommodation</p>
                    <p className="text-xl font-bold text-green-600">{trip.budgetBreakdown.accommodationCost} {trip.currency}</p>
                  </div>

                  <div className="p-4 text-center rounded-xl bg-purple-500/10">
                    <div className="mb-2 text-purple-600">
                      <svg className="mx-auto w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14.828 14.828a4 4 0 01-5.656 0M9 10h1m4 0h1m-6 8a9 9 0 110-18 9 9 0 010 18z" />
                      </svg>
                    </div>
                    <p className="mb-1 text-sm text-base-content/70">Activities</p>
                    <p className="text-xl font-bold text-purple-600">{trip.budgetBreakdown.activitiesCost} {trip.currency}</p>
                  </div>

                  <div className="p-4 text-center rounded-xl bg-primary/10">
                    <div className="mb-2 text-primary">
                      <svg className="mx-auto w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
                      </svg>
                    </div>
                    <p className="mb-1 text-sm text-base-content/70">Total Budget</p>
                    <p className="text-2xl font-bold text-primary">{trip.budgetBreakdown.totalCost} {trip.currency}</p>
                  </div>
                </div>
              ) : (
                <p className="py-8 text-center text-base-content/50">Budget breakdown not available</p>
              )}
            </div>
          </div>
        )}

        {activeTab === 'itinerary' && (
          <div className="p-8 card-modern animate-fade-in">
            <h3 className="mb-8 text-3xl font-bold text-base-content">Daily Itinerary</h3>

            {trip.dailyItineraries && trip.dailyItineraries.length > 0 ? (
              <div className="space-y-8">
                {trip.dailyItineraries.map((day, index) => (
                  <div key={index} className="pl-6 border-l-4 border-primary animate-slide-in" style={{animationDelay: `${index * 0.1}s`}}>
                    <div className="flex gap-4 items-center mb-4">
                      <div className="badge badge-primary badge-lg">Day {index + 1}</div>
                      <h4 className="text-xl font-bold text-base-content">{day.date}</h4>
                    </div>

                    {day.activities && day.activities.map((activity, actIndex) => (
                      <div key={actIndex} className="mb-4 transition-colors duration-200 card bg-base-200/50 hover:bg-base-200">
                        <div className="card-body">
                          <div className="flex flex-col gap-4 md:flex-row md:justify-between md:items-start">
                            <div className="flex-1">
                              <div className="flex gap-2 items-center mb-2">
                                <span className="font-semibold text-primary">{activity.time}</span>
                                <span className="text-base-content/50"></span>
                                <h5 className="text-lg font-bold text-base-content">{activity.name}</h5>
                              </div>
                              <p className="mb-3 text-base-content/70">{activity.description}</p>
                              <div className="flex gap-2 items-center">
                                <span className="font-semibold text-primary">{activity.cost} {trip.currency}</span>
                              </div>
                            </div>
                            <div className="flex-shrink-0">
                              <div className="badge badge-secondary badge-outline">{activity.type}</div>
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ))}
              </div>
            ) : (
              <div className="py-12 text-center">
                <svg className="mx-auto mb-4 w-16 h-16 text-base-content/30" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                </svg>
                <p className="text-lg text-base-content/50">No itinerary available for this trip.</p>
              </div>
            )}
          </div>
        )}

        {activeTab === 'map' && (
          <div className="p-8 text-center card-modern animate-fade-in">
            <div className="mb-6">
              <svg className="mx-auto mb-4 w-16 h-16 text-base-content/30" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 20l-5.447-2.724A1 1 0 013 16.382V5.618a1 1 0 011.447-.894L9 7m0 13l6-3m-6 3V7m6 10l4.553 2.276A1 1 0 0021 18.382V7.618a1 1 0 00-.553-.894L15 4m0 13V4m0 0L9 7" />
              </svg>
              <h3 className="mb-2 text-2xl font-bold text-base-content">Interactive Map</h3>
              <p className="text-base-content/70">Map visualization will be displayed here showing your trip route and points of interest.</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TripResults;
