import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Booking = () => {
  const { tripId } = useParams();
  const navigate = useNavigate();
  const [trip, setTrip] = useState(null);
  const [loading, setLoading] = useState(true);
  const [booking, setBooking] = useState(false);
  const [error, setError] = useState('');
  const [activeStep, setActiveStep] = useState(0);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    cardNumber: '',
  });

  const steps = [
    { id: 'summary', title: 'Trip Summary', icon: 'ðŸ“‹' },
    { id: 'details', title: 'Traveler Details', icon: 'ðŸ‘¤' },
    { id: 'payment', title: 'Payment', icon: 'ðŸ’³' },
    { id: 'confirmation', title: 'Confirmation', icon: 'âœ…' }
  ];

  useEffect(() => {
    const fetchTrip = async () => {
      try {
        const response = await axios.get(`/api/trips/${tripId}`);
        setTrip(response.data);
      } catch (err) {
        setError('Failed to load trip details');
        console.error('Error fetching trip:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTrip();
  }, [tripId]);

  const handleInputChange = (field) => (event) => {
    setFormData({
      ...formData,
      [field]: event.target.value,
    });
  };

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const handleConfirmBooking = async () => {
    setBooking(true);
    setError('');

    try {
      const bookingData = {
        tripId,
        travelerName: formData.name,
        travelerEmail: formData.email,
        travelerPhone: formData.phone,
      };

      const response = await axios.post('/api/booking/book', bookingData);

      if (response.data.success) {
        setActiveStep(3);
      } else {
        setError(response.data.message || 'Booking failed');
      }
    } catch (err) {
      setError('Booking failed. Please try again.');
      console.error('Error booking trip:', err);
    } finally {
      setBooking(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-base-100">
        <div className="card bg-base-100 shadow-xl max-w-md">
          <div className="card-body text-center">
            <span className="loading loading-spinner loading-lg text-primary"></span>
            <h2 className="card-title justify-center text-2xl">Loading trip details...</h2>
            <p className="text-base-content/60">Preparing your booking experience</p>
          </div>
        </div>
      </div>
    );
  }

  if (error && !trip) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-base-100">
        <div className="card bg-base-100 shadow-xl max-w-lg">
          <div className="card-body">
            <div className="alert alert-error">
              <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <div>
                <h3 className="font-bold">Unable to load trip details</h3>
                <div className="text-xs">{error}</div>
              </div>
            </div>
            <button onClick={() => navigate(`/results/${tripId}`)} className="btn btn-primary gap-2">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                <path strokeLinecap="round" strokeLinejoin="round" d="M10 19l-7-7m0 0l7-7m-7 7h18" />
              </svg>
              Back to Results
            </button>
          </div>
        </div>
      </div>
    );
  }

  const renderStepContent = (step) => {
    switch (step) {
      case 0:
        return (
          <div className="space-y-8">
            <div className="text-center mb-8">
              <h2 className="text-4xl font-bold mb-4">Trip Summary</h2>
              <p className="text-xl text-base-content/60">Review your perfect vacation plan</p>
            </div>

            <div className="card bg-base-100 shadow-xl max-w-4xl mx-auto">
              <div className="card-body">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                  <div className="space-y-4">
                    <div className="card bg-info/10 border border-info/20">
                      <div className="card-body">
                        <div className="text-sm font-semibold text-info uppercase mb-2">Destination</div>
                        <div className="text-2xl font-bold">{trip?.destination}</div>
                      </div>
                    </div>
                    <div className="card bg-secondary/10 border border-secondary/20">
                      <div className="card-body">
                        <div className="text-sm font-semibold text-secondary uppercase mb-2">Travel Dates</div>
                        <div className="text-xl font-semibold">
                          {trip?.startDate} - {trip?.endDate}
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="space-y-4">
                    <div className="card bg-success/10 border border-success/20">
                      <div className="card-body">
                        <div className="text-sm font-semibold text-success uppercase mb-2">Transportation</div>
                        <div className="text-xl font-semibold">
                          {trip?.transportation?.type} via {trip?.transportation?.provider}
                        </div>
                      </div>
                    </div>
                    <div className="card bg-warning/10 border border-warning/20">
                      <div className="card-body">
                        <div className="text-sm font-semibold text-warning uppercase mb-2">Accommodation</div>
                        <div className="text-xl font-semibold">
                          {trip?.accommodation?.name} ({trip?.accommodation?.type})
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="card bg-gradient-to-r from-primary/10 to-secondary/10 border-2 border-primary/20">
                  <div className="card-body text-center">
                    <div className="text-sm font-semibold text-base-content/60 uppercase mb-2">Total Cost</div>
                    <div className="text-4xl font-bold text-primary">
                      {trip?.budgetBreakdown?.totalCost} {trip?.currency}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );

      case 1:
        return (
          <div className="space-y-8">
            <div className="text-center mb-8">
              <div className="text-7xl mb-6">ðŸ‘¤</div>
              <h2 className="text-4xl font-bold mb-4">Traveler Details</h2>
              <p className="text-xl text-base-content/60">Please provide your contact information</p>
            </div>

            <div className="card bg-base-100 shadow-xl max-w-3xl mx-auto">
              <div className="card-body space-y-6">
                <div className="form-control">
                  <label className="label">
                    <span className="label-text font-semibold">Full Name</span>
                  </label>
                  <input
                    type="text"
                    placeholder="Enter your full name"
                    className="input input-bordered input-primary w-full"
                    value={formData.name}
                    onChange={handleInputChange('name')}
                    required
                  />
                </div>

                <div className="form-control">
                  <label className="label">
                    <span className="label-text font-semibold">Email Address</span>
                  </label>
                  <input
                    type="email"
                    placeholder="your.email@example.com"
                    className="input input-bordered input-primary w-full"
                    value={formData.email}
                    onChange={handleInputChange('email')}
                    required
                  />
                </div>

                <div className="form-control">
                  <label className="label">
                    <span className="label-text font-semibold">Phone Number</span>
                  </label>
                  <input
                    type="tel"
                    placeholder="+1 (555) 123-4567"
                    className="input input-bordered input-primary w-full"
                    value={formData.phone}
                    onChange={handleInputChange('phone')}
                    required
                  />
                </div>
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="space-y-8">
            <div className="text-center mb-8">
              <div className="text-7xl mb-6">ðŸ’³</div>
              <h2 className="text-4xl font-bold mb-4">Payment Information</h2>
              <p className="text-xl text-base-content/60">Secure payment processing</p>
            </div>

            <div className="alert alert-info max-w-2xl mx-auto mb-6">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" className="stroke-current shrink-0 w-6 h-6">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <div>
                <h3 className="font-bold">Demo Mode</h3>
                <div className="text-xs">This is a demo application. No real payment will be processed.</div>
              </div>
            </div>

            <div className="card bg-base-100 shadow-xl max-w-3xl mx-auto">
              <div className="card-body space-y-6">
                <div className="form-control">
                  <label className="label">
                    <span className="label-text font-semibold">Card Number</span>
                  </label>
                  <input
                    type="text"
                    placeholder="4111 1111 1111 1111"
                    className="input input-bordered input-primary w-full"
                    value={formData.cardNumber}
                    onChange={handleInputChange('cardNumber')}
                    required
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="form-control">
                    <label className="label">
                      <span className="label-text font-semibold">Expiry Date</span>
                    </label>
                    <input
                      type="text"
                      placeholder="MM/YY"
                      className="input input-bordered input-primary w-full"
                    />
                  </div>

                  <div className="form-control">
                    <label className="label">
                      <span className="label-text font-semibold">CVV</span>
                    </label>
                    <input
                      type="text"
                      placeholder="123"
                      className="input input-bordered input-primary w-full"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        );

      case 3:
        return (
          <div className="py-16 text-center">
            <div className="card bg-base-100 shadow-xl max-w-4xl mx-auto">
              <div className="card-body py-16">
                <div className="mb-8">
                  <div className="text-9xl mb-8">ðŸŽ‰</div>
                  <h2 className="text-5xl font-bold mb-6 text-success">Booking Confirmed!</h2>
                  <p className="mx-auto max-w-3xl text-2xl leading-relaxed text-base-content/70 mb-12">
                    Your trip has been successfully booked. You will receive a confirmation email shortly with all the details.
                  </p>
                </div>

                <div className="flex flex-col sm:flex-row gap-4 justify-center">
                  <button
                    onClick={() => navigate('/saved')}
                    className="btn btn-primary btn-lg gap-2"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-6 h-6">
                      <path strokeLinecap="round" strokeLinejoin="round" d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
                    </svg>
                    View Saved Trips
                  </button>

                  <button
                    onClick={() => navigate('/')}
                    className="btn btn-outline btn-lg gap-2"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-6 h-6">
                      <path strokeLinecap="round" strokeLinejoin="round" d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
                    </svg>
                    Plan Another Trip
                  </button>
                </div>
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-base-100 py-8">
      {/* Hero Header */}
      <div className="hero bg-gradient-to-r from-primary to-secondary text-primary-content rounded-3xl shadow-2xl mx-4 mb-8">
        <div className="hero-content py-12">
          <div className="max-w-5xl w-full">
            <button onClick={() => navigate(`/results/${tripId}`)} className="btn btn-ghost btn-sm mb-6 gap-2 bg-base-100/20 hover:bg-base-100/30">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                <path strokeLinecap="round" strokeLinejoin="round" d="M10 19l-7-7m0 0l7-7m-7 7h18" />
              </svg>
              Back to Results
            </button>

            <h1 className="mb-6 text-5xl font-bold md:text-6xl">Complete Your Booking</h1>
            <p className="text-xl opacity-90">
              Follow these simple steps to secure your perfect vacation
            </p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container mx-auto px-4 max-w-5xl">
        {/* Progress Steps */}
        <ul className="steps steps-horizontal w-full mb-12">
          {steps.map((step, index) => (
            <li
              key={step.id}
              className={`step ${activeStep >= index ? 'step-primary' : ''} ${activeStep > index ? 'step-success' : ''}`}
            >
              <div className="flex flex-col items-center gap-2">
                <span className="text-2xl">{step.icon}</span>
                <span className="hidden sm:inline text-sm font-semibold">{step.title}</span>
              </div>
            </li>
          ))}
        </ul>

        {/* Error Alert */}
        {error && (
          <div className="alert alert-error mb-8 shadow-lg">
            <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <div>
              <h3 className="font-bold">Booking Error</h3>
              <div className="text-xs">{error}</div>
            </div>
          </div>
        )}

        {/* Step Content */}
        <div className="min-h-[600px] mb-8">
          {renderStepContent(activeStep)}
        </div>

        {/* Navigation */}
        {activeStep < 3 && (
          <div className="flex flex-col sm:flex-row justify-between items-center gap-4 pt-8 border-t border-base-300">
            <button
              onClick={handleBack}
              disabled={activeStep === 0}
              className="btn btn-outline gap-2"
            >
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                <path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" />
              </svg>
              Back
            </button>

            <div className="text-lg font-semibold">
              Step {activeStep + 1} of {steps.length}
            </div>

            <button
              onClick={activeStep === 2 ? handleConfirmBooking : handleNext}
              disabled={booking}
              className="btn btn-primary gap-2"
            >
              {booking ? (
                <>
                  <span className="loading loading-spinner"></span>
                  Processing...
                </>
              ) : activeStep === 2 ? (
                <>
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  Confirm Booking
                </>
              ) : (
                <>
                  Next
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
                  </svg>
                </>
              )}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Booking;
